def get_text(key: str, lang: str = None) -> str:
    """Get translated text for a key"""
    if lang is None:
        lang = DEFAULT_LANGUAGE
    if key in TRANSLATIONS:
        return TRANSLATIONS[key].get(lang, TRANSLATIONS[key].get(DEFAULT_LANGUAGE, key))
    return key

def t(key: str, lang: str = None) -> str:
    """Shorthand for get_text"""
    return get_text(key, lang)

def get_language_options() -> list:
    """Get list of language options for selectbox"""
    return [f"{LANGUAGES[code]['flag']} {LANGUAGES[code]['name']}" for code in LANGUAGES]

def get_language_code(selection: str) -> str:
    """Get language code from selection string"""
    for code, info in LANGUAGES.items():
        if f"{info['flag']} {info['name']}" == selection:
            return code
    return DEFAULT_LANGUAGE

def is_rtl(lang: str) -> bool:
    """Check if language is RTL"""
    return LANGUAGES.get(lang, {}).get("rtl", False)

def get_current_language() -> str:
    """Get current language from session state"""
    import streamlit as st
    return st.session_state.get('language', DEFAULT_LANGUAGE)
